    while (std::getline(inputFile, line)) {
        vector&lt;char&gt; vc;
        for (char c : line)
        {
            if((('1'&lt;=c)&amp;&amp;(c&lt;='9'))||c=='.') 
                vc.push_back(c);
        }
        input.push_back(vc);
    }

    inputFile.close(); // Close the file
    printf("------ IN -------\n");
    print_2d_vector&lt;char&gt;(input, "%c ");
    printf("------ OUT ------\n");
    solveSudoku(input);
